mapper.select:
    	set @rownum=#{pageNum};
    	SELECT @rownum:=@rownum+1 as id,tc.name,tx.HRV,tx.sbp_ave,tx.dbp_ave,tx.Heartrate,tx.Bloodoxygen,tx.microcirculation,tx.respirationrate,tx.phone,DATE_FORMAT(tx.createtime,'%Y-%m-%d %H:%i:%s') as createtime FROM(
		SELECT HRV,sbp_ave,dbp_ave,Heartrate,Bloodoxygen,microcirculation,respirationrate,substring(tp.phone,9) as phone,tt.createtime FROM jfhealth tp
		RIGHT JOIN(
		SELECT MAX(createtime) as createtime FROM jfhealth WHERE DATE_FORMAT(createtime,'%Y')=DATE_FORMAT(CURDATE(),'%Y') GROUP BY phone
		) tt
		ON tp.createtime=tt.createtime
		)tx
		LEFT JOIN user tc
		ON tx.phone=tc.id
		LIMIT #{pageNum},#{pageSize}

SELECT tp.imei,tp.id,tt.name,Heartrate,sbp_ave,dbp_ave,microcirculation,respirationrate,Bloodoxygen,HRV,DATE_FORMAT(tp.createtime,'%Y-%m-%d %H:%i:%s') as createtime FROM jfhealth tp
LEFT JOIN equipment td
ON tp.imei=td.imei
LEFT JOIN user tt
ON substring(tp.phone,9)=tt.id
WHERE td.agentid=#{agentid}

AND tp.id=#{id}


AND tt.name=#{name}

ORDER BY createtime DESC
LIMIT 0,16
