mapper.select:
set @rownum=#{pageNum}; SELECT @rownum:=@rownum+1 as id,tp.id as equipmentId,tt.id AS userId,tp.imei,tt.name,tt.gender,td.bed,tc.nurseName,bluetooth_type,tp.bluetooth_status,tt.love,tt.calibration,tk.allNotifyOn FROM equipment tp INNER JOIN (select * from user where isDelete=0 and role='使用者') tt ON tp.imei=tt.imei LEFT JOIN user_eq tn ON tp.id=tn.eq_id AND tn.typeof=0 LEFT JOIN push tk ON tt.id=tk.userId AND tn.user_id=tk.alias LEFT JOIN bed_number td ON tt.bedId=td.id LEFT JOIN nurse tc ON tt.nurseId=tc.id LEFT JOIN admin tz ON tz.agentId=tp.agentid WHERE tz.id=#{agentid}
AND tp.imei LIKE CONCAT('%',#{imeiNum},'%')
AND tt.name LIKE CONCAT('%',#{userName},'%')
LIMIT #{pageNum},#{pageSize}

SELECT COUNT(*) FROM( SELECT tp.id as equipmentId,tt.id AS userId,tp.imei,tt.name,tt.gender,td.bed,tc.nurseName,tp.bluetooth_status,tt.love,tt.calibration,tk.allNotifyOn FROM equipment tp INNER JOIN user tt ON tp.imei=tt.imei LEFT JOIN user_eq tn ON tp.id=tn.eq_id AND tn.typeof=0 LEFT JOIN push tk ON tt.id=tk.userId AND tn.user_id=tk.alias LEFT JOIN bed_number td ON tt.bedId=td.id LEFT JOIN nurse tc ON tt.nurseId=tc.id LEFT JOIN admin tz ON tz.agentId=tp.agentid WHERE tz.id=#{agentid} AND tt.isDelete=0
AND tp.imei LIKE CONCAT('%',#{imeiNum},'%')
) tq

mapper.delete:
DELETE FROM chat WHERE imei=#{imei};
DELETE FROM jfhealthdao WHERE imei=#{imei} and phone=#{phone};
DELETE FROM positionig WHERE imei=#{imei};
DELETE FROM push WHERE userId=#{userId};
DELETE FROM sensorstatus WHERE imei=#{imei};
DELETE FROM usercode WHERE phoen=#{account};
DELETE FROM user_eq WHERE eq_id=#{equipmentId} and typeof=0;
DELETE FROM user_eq WHERE eq_id=#{equipmentId} and typeof=2;
